$.editable.addInputType("time",{element:function(f,d){var b=$('<select id="hour_" />');var h=$('<select id="min_" />');for(var a=0;a<=23;a++){if(a<10){a="0"+a}var e=$("<option />").val(a).append(a);b.append(e)}$(this).append(b);for(var c=0;c<=45;c=parseInt(c,10)+15){if(c<10){c="0"+c}var e=$("<option />").val(c).append(c);h.append(e)}$(this).append(h);var g=$('<input type="hidden" />');$(this).append(g);return(g)},content:function(b,e,d){var a=parseInt(b.substr(0,2),10);var c=parseInt(b.substr(3,2),10);$("#hour_",this).children().each(function(){if(a==$(this).val()){$(this).attr("selected","selected")}});$("#min_",this).children().each(function(){if(c==$(this).val()){$(this).attr("selected","selected")}})},submit:function(b,a){var c=$("#hour_").val()+":"+$("#min_").val();$("input",this).val(c)}});